\\u1ee9u v\\u00e0 gi\\u1ea3ng d\\u1ea1y ng\\u00e0nh t\\u00e0i nguy\\u00ean n\\u01b0\\u1edbc t\\u1ea1i Trung t\\u00e2m kh\\u00ed t\\u01b0\\u1ee3ng thu\\u1ef7 v\\u0103n v\\u00e0 vi\\u1ec5n th\\u00e1m t\\u1ea1i University of California \\u2013 Irvine, chuy\\u00ean s\\u00e2u v\\u1ec1 \\u1ee9ng d\\u1ee5ng c\\u00f4ng ngh\\u1ec7 vi\\u1ec5n th\\u00e1m v\\u00e0 x\\u00e2y d\\u1ef1ng m\\u00f4 h\\u00ecnh to\\u00e1n cho c\\u1ea3nh b\\u00e1o thi\\u00ean tai (<a href=\\\"http:\\/\\/engineering.uci.edu\\/users\\/phu-nguyen\\\">http:\\/\\/engineering.uci.edu\\/users\\/phu-nguyen<\\/a>). TS. Ph\\u00fa c\\u00f3 nhi\\u1ec1u \\u00a0kinh nghi\\u1ec7m trong vi\\u1ec7c x\\u00e2y d\\u1ef1ng c\\u00e1c c\\u01a1 s\\u1edf d\\u1eef li\\u1ec7u v\\u1ec1 vi\\u1ec5n th\\u00e1m, t\\u00e0i nguy\\u00ean thi\\u00ean nhi\\u00ean v\\u00e0 ngu\\u1ed3n l\\u1ef1c \\u0111\\u00e1m \\u0111\\u00f4ng cho \\u00a0quan tr\\u1eafc v\\u00e0 c\\u1ea3nh b\\u00e1o thi\\u00ean tai. TS. Ph\\u00fa l\\u00e0 ng\\u01b0\\u1eddi \\u0111\\u1ed3ng s\\u00e1ng l\\u1eadp, T\\u1ed5ng th\\u01b0 k\\u00fd (2013-2015), Ch\\u1ee7 t\\u1ecbch (2015-2017) c\\u1ee7a \\u00a0H\\u1ed9i thanh ni\\u00ean \\u2013 sinh vi\\u00ean Vi\\u1ec7t Nam t\\u1ea1i Hoa K\\u1ef3.<\\/span><\\/span><\\/p>\\r\\n<p>\\u00a0<\\/p>\\r\\n<p>\\u00a0<\\/p>\\r\\n<p style=\\\"text-align: justify;\\\"><b><img style=\\\"float: left;\\\" src=\\\"images\\/Ve_chung_toi\\/Kieu_Quoc_Khanh.png\\\" width=\\\"140\\\" height=\\\"140\\\" \\/><\\/b><\\/p>\\r\\n<p dir=\\\"ltr\\\" style=\\\"text-align: justify;\\\"><b>\\u00a0 \\u00a0TS. Ki\\u1ec1u Qu\\u1ed1c Ch\\u00e1nh <\\/b>hi\\u1ec7n \\u0111ang l\\u00e0 Assistant Professor t\\u1ea1i tr\\u01b0\\u1eddng \\u0110\\u1ea1i H\\u1ecdc Indiana, Bloomington. TS. Ch\\u00e1nh nghi\\u00ean c\\u1ee9u v\\u00e0 gi\\u1ea3ng d\\u1ea1y ch\\u1ee7 y\\u1ebfu v\\u1ec1 b\\u00e0i to\\u00e1n \\u0111\\u1ed9ng l\\u1ef1c phi tuy\\u1ebfn v\\u00e0 m\\u00f4 h\\u00ecnh h\\u00f3a b\\u00e3o, c\\u01a1 ch\\u1ebf ph\\u00e1t sinh c\\u1ee7a b\\u00e3o, v\\u00e0 t\\u00ednh d\\u1ef1 b\\u00e1o c\\u01b0\\u1eddng \\u0111\\u1ed9 c\\u0169ng nh\\u01b0 chuy\\u1ec3n \\u0111\\u1ed9ng c\\u1ee7a b\\u00e3o. C\\u00f9ng v\\u1edbi c\\u00e1c nghi\\u00ean c\\u1ee9u c\\u01a1 b\\u1ea3n v\\u1ec1 b\\u00e3o, TS. Ch\\u00e1nh c\\u0169ng tham gia ph\\u00e1t tri\\u1ec3n c\\u00e1c thu\\u1eadt to\\u00e1n \\u0111\\u1ed3ng h\\u00f3a s\\u1ed1 li\\u1ec7u quan tr\\u1eafc v\\u1ec7 tinh \\u0111\\u1ec3 c\\u1ea3i thi\\u1ec7n d\\u1ef1 b\\u00e1o b\\u00e3o v\\u00e0 th\\u1eddi ti\\u1ebft c\\u1ef1c \\u0111oan. Tr\\u01b0\\u1edbc khi tham gia tr\\u01b0\\u1eddng \\u0110\\u1ea1i H\\u1ecdc Indiana, Bloomington n\\u0103m 2015, TS. Ch\\u00e1nh \\u0111\\u00e3 gi\\u1eef c\\u00e1c v\\u1ecb tr\\u00ed kh\\u00e1c nhau bao g\\u1ed3m v\\u1ecb tr\\u00ed nghi\\u00ean c\\u1ee9u vi\\u00ean t\\u1ea1i tr\\u01b0\\u1eddng \\u0110\\u1ea1i H\\u1ecdc Maryland, sau ti\\u1ebfn s\\u0129 t\\u1ea1i tr\\u01b0\\u1eddng \\u0110\\u1ea1i H\\u1ecdc Pennsylvania, gi\\u1ea3ng vi\\u00ean t\\u1ea1i tr\\u01b0\\u1eddng \\u0110H Khoa H\\u1ecdc T\\u1ef1 Nhi\\u00ean H\\u00e0 N\\u00f4i (2010-2013), nghi\\u00ean c\\u1ee9u vi\\u00ean \\u1edf trung t\\u00e2m d\\u1ef1 b\\u00e1o m\\u00f4i tr\\u01b0\\u1eddng c\\u1ee7a Hoa K\\u1ef3 (NCEP), v\\u00e0 v\\u1ecb tr\\u00ed ph\\u00e1t tri\\u1ec3n m\\u00f4 h\\u00ecnh d\\u1ef1 b\\u00e1o s\\u1ed1 c\\u1ee7a t\\u1eadp \\u0111o\\u00e0n IMSG.<\\/p>\\r\\n<p style=\\\"text-align: justify;\\\">\\u00a0<\\/p>\\r\\n<p><img style=\\\"float: left;\\\" src=\\\"images\\/Ve_chung_toi\\/Trinh_Tuan_Long.jpg\\\" width=\\\"140\\\" height=\\\"140\\\" \\/><\\/p>\\r\\n<p style=\\\"text-align: justify;\\\"><b>\\u00a0 NCS. Tr\\u1ecbnh Tu\\u1ea5n Long<\\/b><span style=\\\"font-weight: 400;\\\"> \\u0111ang la\\u0300 nghi\\u00ean c\\u01b0\\u0301u sinh ta\\u0323i khoa \\u0110i\\u0323a Ly\\u0301, tr\\u01b0\\u01a1\\u0300ng \\u0110a\\u0323i ho\\u0323c thu\\u0309 \\u0111\\u00f4 Tokyo, Nh\\u00e2\\u0323t Ba\\u0309n. Anh Long cu\\u0303ng nh\\u00e2\\u0323n \\u0111\\u01b0\\u01a1\\u0323c ho\\u0323c b\\u00f4\\u0309ng cu\\u0309a chi\\u0301nh quy\\u00ea\\u0300n thu\\u0309 \\u0111\\u00f4 Tokyo cho vi\\u00ea\\u0323c ho\\u0323c t\\u00e2\\u0323p va\\u0300 nghi\\u00ean c\\u01b0\\u0301u cu\\u0309a mi\\u0300nh trong ba n\\u0103m 2015- 2018. Hi\\u00ea\\u0323n ta\\u0323i, ca\\u0301c h\\u01b0\\u01a1\\u0301ng nghi\\u00ean c\\u01b0\\u0301u chi\\u0301nh anh Long \\u0111ang t\\u00e2\\u0323p trung bao g\\u00f4\\u0300m m\\u00f4 hi\\u0300nh ho\\u0301a khi\\u0301 h\\u00e2\\u0323u khu v\\u01b0\\u0323c va\\u0300 nghi\\u00ean c\\u01b0\\u0301u ca\\u0301c ta\\u0301c \\u0111\\u00f4\\u0323ng cu\\u0309a bi\\u00ea\\u0301n \\u0111\\u00f4\\u0309i khi\\u0301 h\\u00e2\\u0323u \\u0111\\u00ea\\u0301n ca\\u0301c hi\\u00ea\\u0323n t\\u01b0\\u01a1\\u0323ng m\\u01b0a c\\u01b0\\u0323c tri\\u0323, m\\u01b0a l\\u01a1\\u0301n \\u01a1\\u0309 Vi\\u00ea\\u0323t Nam. Ngoa\\u0300i ra, anh Long t\\u01b0\\u0300ng tham gia m\\u00f4\\u0323t s\\u00f4\\u0301 d\\u01b0\\u0323 a\\u0301n va\\u0300 cu\\u0303ng co\\u0301 kinh nghi\\u00ea\\u0323m nghi\\u00ean c\\u01b0\\u0301u v\\u00e2\\u0301n \\u0111\\u00ea\\u0300 m\\u00f4 pho\\u0309ng ba\\u0303o trong ca\\u0301c m\\u00f4 hi\\u0300nh khi\\u0301 h\\u00e2\\u0323u khu v\\u01b0\\u0323c\\u00a0d\\u01b0\\u0323 ba\\u0301o mu\\u0300a va\\u0300 \\u0111a\\u0301nh gia\\u0301, ph\\u00e2n ti\\u0301ch ca\\u0301c sa\\u0309n ph\\u00e2\\u0309m m\\u01b0a v\\u00ea\\u0323 tinh cho khu v\\u01b0\\u0323c Vi\\u00ea\\u0323t Nam.<\\/span><\\/p>\\r\\n<p style=\\\"text-align: justify;\\\">\\u00a0<\\/p>\\r\\n<p style=\\\"text-align: justify;\\\"><span style=\\\"font-weight: 400;\\\">{jcomments on}<\\/span><\\/p>\",\"fulltext\":\"\",\"state\":1,\"catid\":\"22\",\"created\":\"2018-01-30 09:10:20\",\"created_by\":\"56\",\"created_by_alias\":\"\",\"modified\":\"2018-03-22 12:44:21\",\"modified_by\":\"56\",\"checked_out\":\"56\",\"checked_out_time\":\"2018-03-22 12:32:53\",\"publish_up\":\"2018-01-30 09:10:20\",\"publish_down\":\"0000-00-00 00:00:00\",\"images\":\"{\\\"image_intro\\\":\\\"\\\",\\\"float_intro\\\":\\\"\\\",\\\"image_int